query:
-- อัพเดต fullName จาก firstNameEn และ lastNameEn
UPDATE workers 
SET fullName = CONCAT(COALESCE(firstNameEn, ''), ' ', COALESCE(lastNameEn, ''))
WHERE fullName IS NULL OR fullName = '';

-- ลบข้อมูลนายจ้างและลูกจ้างเก่าทั้งหมดเพื่อเริ่มใหม่
DELETE FROM workers;
DELETE FROM employers;

